import React, { useState, useEffect } from "react";
import Select from "react-select";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { fetchCities } from "../reducers/cities";
import order from "../reducers/order";
import { createSelectOptions, printThis } from "../constants/functions";

const SelectContainer = styled.form`
  width: 200px;
`;

const InputForm = () => {
  // ----- variables -----
  const dispatch = useDispatch();
  const store = useSelector((store) => store.cities.list);
  const [city, setCity] = useState([]);

  // we create an array that is shown inside the Select
  const options = createSelectOptions(store);

  // we call thunk in redux store to fetch from API
  useEffect(() => {
    dispatch(fetchCities());
  }, [dispatch]);

  // Rendering the element
  return (
    <SelectContainer onSubmit={(event) => printThis(event, city)}>
      <Select
        width="200px"
        options={options}
        onChange={(event) => setCity(event.value)}
      />
      <button type="submit">Submit this MF</button>
    </SelectContainer>
  );
};

export default InputForm;
