import React, { useState, useEffect } from "react";
import Select from "react-select";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import cities, { fetchCities } from "../reducers/cities";

const SelectContainer = styled.form`
  width: 200px;
`;

const InputForm = () => {
  const dispatch = useDispatch();
  const store = useSelector((store) => store.cities.list);
  console.log(store);

  const [city, setCity] = useState("");
  const [cityList, setCityList] = useState([]);

  // useEffect(() => {
  //   fetch("http://localhost:8080/cities")
  //     .then((res) => res.json())
  //     .then((json) => {
  //       setCityList(
  //         json.response.map((item) => {
  //           return { value: item.cityName, label: item.cityName };
  //         })
  //       );
  //       setLoading(false);
  //     });
  // }, []);

  useEffect(() => {
    dispatch(fetchCities());
  }, []);

  // if (store) {
  //   setCityList(
  //     store.map((item) => {
  //       return { value: item.cityName, label: item.cityName };
  //     })
  //   );
  // }

  const printThis = (event) => {
    event.preventDefault();
  };
  return (
    <SelectContainer onSubmit={(event) => printThis(event)}>
      <Select
        width="200px"
        options={cityList}
        onChange={(event) => setCity(event.value)}
      />
      <button type="submit">Submit this MF</button>
    </SelectContainer>
  );
};

export default InputForm;
