import React, { useState, useEffect } from "react";
import Select from "react-select";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { fetchCities } from "../reducers/cities";

const SelectContainer = styled.form`
  width: 200px;
`;

const InputForm = () => {
  // ----- variables -----
  const dispatch = useDispatch();
  const store = useSelector((store) => store.cities.list);
  const [city, setCity] = useState([]);

  const createSelectOptions = (array) => {
    array.map((item) => {
      return { value: item.cityName, label: item.cityName };
    });
  };

  // we are reading the array from store and maping over to create options that are shown inside the Select
  const options = store.map((item) => {
    return { value: item.cityName, label: item.cityName };
  });

  // we call thunk in redux store to fetch from API
  useEffect(() => {
    dispatch(fetchCities());
  }, [dispatch]);

  // Placeholder function to be used to move to the next page after clicking
  const printThis = (event) => {
    event.preventDefault();
    console.log(city);
  };

  // MRendering the element
  return (
    <SelectContainer onSubmit={(event) => printThis(event)}>
      <Select
        width="200px"
        options={options}
        onChange={(event) => setCity(event.value)}
      />
      <button type="submit">Submit this MF</button>
    </SelectContainer>
  );
};

export default InputForm;
