import React, { useState } from "react";
import Select from "react-select";
import styled from "styled-components";

const InputForm = () => {
  const SelectContainer = styled.form`
    width: 200px;
  `;

  const [city, setCity] = useState("riga");

  const options = [
    { value: "riga", label: "R카ga" },
    { value: "liepaja", label: "Liep훮ja" },
    { value: "daugavpils", label: "Daugavpils" },
  ];

  const handleInputForm = (event) => {
    event.preventDefault();
    setCity(event.target.value);
  };
  return (
    <SelectContainer onSubmit={(event) => handleInputForm(event)}>
      <Select
        width="200px"
        options={options}
        isSearchable={true}
        onChange={(event) => handleInputForm}
      />
    </SelectContainer>
  );
};

export default InputForm;
