import React, { useState, useEffect } from "react";
import Select from "react-select";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import cities, { fetchCities } from "../reducers/cities";

const SelectContainer = styled.form`
  width: 200px;
`;

const InputForm = () => {
  const dispatch = useDispatch();
  const store = useSelector((store) => store.cities.list);

  const [city, setCity] = useState("");
  const [cityList, setCityList] = useState([]);

  // we call thunk in redux store to fetch from API
  useEffect(() => {
    dispatch(fetchCities());
  }, []);

  console.log(city);

  if (store) {
    setCityList(
      store.map((item) => {
        return { value: item.cityName, label: item.cityName };
      })
    );
  }

  const printThis = (event) => {
    event.preventDefault();
  };
  return (
    <SelectContainer onSubmit={(event) => printThis(event)}>
      <Select
        width="200px"
        options={cityList}
        onChange={(event) => setCity(event.value)}
      />
      <button type="submit">Submit this MF</button>
    </SelectContainer>
  );
};

export default InputForm;
