import React, { useState } from "react";
import Select from "react-select";
import styled from "styled-components";

const SelectContainer = styled.form`
  width: 200px;
`;

const InputForm = () => {
  const [city, setCity] = useState("");

  const options = [
    { value: "riga", label: "R카ga" },
    { value: "liepaja", label: "Liep훮ja" },
    { value: "daugavpils", label: "Daugavpils" },
  ];

  const handleInputForm = (event) => {
    // setCity(event.value);
    console.log(event);
    setCity(event.value);
  };

  const printThis = () => {
    console.log(city);
  };
  return (
    <SelectContainer onSubmit={() => printThis()}>
      <Select
        width="200px"
        options={options}
        onChange={(event) => handleInputForm(event)}
      />
      <button type="submit">Submit this MF</button>
    </SelectContainer>
  );
};

export default InputForm;
