import React, { useState, useEffect } from "react";
import Select from "react-select";
import styled from "styled-components";

const SelectContainer = styled.form`
  width: 200px;
`;

const InputForm = () => {
  const [city, setCity] = useState("");
  const [cityList, setCityList] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:8080/cities")
      .then((res) => res.json())
      .then((json) => {
        setCityList(json);
        setLoading(false);
      });
  }, []);

  console.log(cityList);

  const options = [
    { value: "riga", label: "R카ga" },
    { value: "liepaja", label: "Liep훮ja" },
    { value: "daugavpils", label: "Daugavpils" },
  ];

  const printThis = (event) => {
    event.preventDefault();
    console.log(city);
  };
  return (
    <SelectContainer onSubmit={(event) => printThis(event)}>
      <Select
        width="200px"
        options={options}
        onChange={(event) => setCity(event.value)}
      />
      <button type="submit">Submit this MF</button>
    </SelectContainer>
  );
};

export default InputForm;
